import React from 'react';
import '../style/login.css';
import { Link } from 'react-router-dom';
import { useState } from 'react';
import { useSelector } from 'react-redux';
import axios from 'axios';

export default function Login() {
// const user = useSelector(state => state.user.userInfo);
// console.log(user);
const [email, setEmail] =useState('');
const [password, setPassword] =useState('');


  const handleLogin = async()=>{
    try{
      const login = await axios.post('http://127.0.0.1:5500/api/v1/users/login',{email, password});
      console.log(login.data);
    }catch(err){
      console.log(err);
    } 
  }

  return (
    <div className='login'>
        <div className='loginWrapper'>
            <form  className="loginForm"  onSubmit={handleLogin}>
                <span className="loginInfo"><strong>Sign in</strong></span>
                <label >Email</label>
                <input type="text" className='loginInput' placeholder='abc@email.com' onChange={e => setEmail(e.target.value)}/>
                <label >Password</label>
                <input type="password" className='loginInput' onChange={e =>setPassword(e.target.value)}/>
                <button className="loginBtn">Login</button>
                <span className="loginPassForget">Forgot Password ?</span>
            </form>
            <Link to='/register'>
            <button className='loginregisterBtn'>Register</button>
            </Link>
        </div>
    </div>
  )
}
